import React from "react";
import s from './Users.module.css';
import {UsersPropsType} from "./UsersContainer";

const Users = (props: UsersPropsType) => {

    let pageCount: number = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages: Array<number> = [];

    for (let i = 1; i <= pageCount; i++) {
        pages.push(i)
        return <div>
            <div>
                {pages.map(p => {
                    return <span className={props.currentPage === p ? s.selectedPage : ''}
                                 onClick={(event) => onChangeNumber(p)}>{p}</span>
                })}
            </div>
            {props.users.map(u =>
                <div key={u.id}>
                <span>
                    <div>
                        < img
                            src={u.photos.small !== null ? u.photos.small : "https://i.pinimg.com/originals/bd/bf/d0/bdbfd0d90f12604b1538e2049f1c981c.jpg"}
                            className={s.img}/>
                    </div>
                    <div>
                        {u.followed
                            ? <button onClick={() => {
                                props.follow(u.id)
                            }}>Follow</button>
                            : <button onClick={() => {
                                props.unfollow(u.id)
                            }}>Unfollow</button>
                        }
                    </div>
                </span>
                    <span>
                    <span>
                        <div>{u.name}</div>
                        <div>{u.status}</div>
                    </span>
                    <span>
                        <div>{'u.location.country'}</div>
                        <div>{'u.location.city'}</div>
                    </span>
                </span>
                </div>
            )}
        </div>
    }
}

export default Users
