const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'
const SET_USERS = 'SET-USERS'

export type UsersType = {
    id: number
    followed: boolean
    fullName: string
    status: string
    location: {city: string,
        country: string}
    photoUrl: string
}

export type InitialStateUsersType = {
    users: Array<UsersType>
}
let initialState: InitialStateUsersType = {users:[
        {id: 1, followed: true,  fullName: 'Dima', status: 'i am a Boss', location: {city: 'Minsk', country: 'Belorus'},photoUrl: 'https://i.pinimg.com/originals/bd/bf/d0/bdbfd0d90f12604b1538e2049f1c981c.jpg'},
        {id: 2, followed: false,  fullName: 'Petr', status: 'hello', location: {city: 'Kiev', country: 'Ukraina'},photoUrl: 'https://i.pinimg.com/originals/bd/bf/d0/bdbfd0d90f12604b1538e2049f1c981c.jpg'},
        {id: 3, followed: true,  fullName: 'Valik', status: 'i am a Boss', location: {city: 'Kiev', country: 'Ukraina'},photoUrl: 'https://i.pinimg.com/originals/bd/bf/d0/bdbfd0d90f12604b1538e2049f1c981c.jpg'},
        {id: 4, followed: false,  fullName: 'Ira', status: 'let speak', location: {city: 'Moscow', country: 'Russia'},photoUrl: 'https://i.pinimg.com/originals/bd/bf/d0/bdbfd0d90f12604b1538e2049f1c981c.jpg'},
        {id: 5, followed: true,  fullName: 'Dasha', status: 'i am a clever', location: {city: 'Moskow', country: 'Russia'},photoUrl: 'https://i.pinimg.com/originals/bd/bf/d0/bdbfd0d90f12604b1538e2049f1c981c.jpg'},
    ]
}


const usersReducer = (state: InitialStateUsersType = initialState, action: ActionsType): InitialStateUsersType => {
    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userID) {
                        return {...u, followed: false}
                    }
                    return u
                })
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userID) {
                        return {...u, followed: true}
                    } return u
                })
            }
        case SET_USERS:
            return {...state, users: [ ...state.users, ...action.newUsers]}
        default:
            return state
    }
}

export const followAC = (userID: number) => ({type: FOLLOW, userID } as const)
export const unFollowAC = (userID: number) => ({type: UNFOLLOW, userID} as const)
export const setUsersAC = (newUsers: Array<UsersType>) => ({type: SET_USERS, newUsers} as const)
type ActionsType =
    | ReturnType<typeof followAC>
    | ReturnType<typeof unFollowAC>
    | ReturnType<typeof setUsersAC>

export default usersReducer
