import React from 'react';
import s from './ProfilInfo.module.css';
import {updateStatus} from "../../../Redux/profilReducer";

type PropsType = {
    status: string
    updateStatus: (status: string) => void
}

class ProfileStatus extends React.Component<PropsType> {
    statusInputRef: React.RefObject<unknown> = React.createRef()
    state = {
        editMode: false
    }

    activateEditMode = ()  => {
        this.setState({
            editMode: true
        })
    }

    deactivateEditMode = () => {
        this.setState({
            editMode: false
        })

        this.props.updateStatus(this.statusInputRef.current.value)
    }

    render() {

        return (
            <div>
                {!this.state.editMode
                    && <div>
                        <span onDoubleClick={this.activateEditMode}> {this.props.status}</span>
                    </div>}
                {
                    this.state.editMode && <div>
                        <input ref={this.statusInputRef} autoFocus={true} onBlur={this.deactivateEditMode} value={this.props.status}/>
                    </div>

                }
            </div>
        )
    }
}


export default ProfileStatus;
