import React from 'react';
import {connect} from "react-redux";
import axios from "axios";
import Header from "./Header";
import {AllAppStateType} from "../../Redux/redux-store";
import {setAuthUserData} from "../../Redux/authReducer";


export type UsersPropsType = MapStatePropsType & MapDispathPropsType

type MapStatePropsType = {
    data: {
        id: number | null
        email: string | null
        login: string | null
    }
}
type MapDispathPropsType = {
setAuthUserData: (data: {
    id: number | null
    email: string | null
    login: string | null
}) => void
}

class HeaderContainer extends React.Component<UsersPropsType> {

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/auth/me`, {withCredentials: true})
            .then(response => {
                if (response.data.resultCode === 0) {
                    let data = response.data.data.login
                    this.props.setAuthUserData(data)
                }
            })
    }

    render() {
        return <Header  />
    }
}

let mapStateToProps = (state: AllAppStateType): MapStatePropsType => {
    return {
        data: state.auth.data
    }
}



export default connect(mapStateToProps, {})(HeaderContainer)
