import {ActionsTypes, PostType, StateType} from "./State";

const ADD_POST = 'ADD-POST'
const UPDATE_NEW_POST = 'UPDATE-NEW-POST'

export const addPostActionCreator = () => ({type: ADD_POST})
export const updateNewPostTextActionCreator = (text: string) =>
    ({type: UPDATE_NEW_POST, postText: text})

const profilReducer = (state: StateType, action: any) => {
    switch (action.type) {
        case ADD_POST:
            const newPost: PostType = {
                id: 4,
                message: state.profilPage.newPostText,
                likecount: '0'
            };
            state.profilPage.posts.push(newPost)
            state.profilPage.newPostText = ''                                                                                     // дает  пустую строку
            return state
        case UPDATE_NEW_POST:
            state.profilPage.newPostText = action.postText
            return state
        default:
            return state
    }
}

export default profilReducer
