import React from 'react';
import {connect} from "react-redux";
import axios from "axios";
import Header from "./Header";
import {AllAppStateType} from "../../Redux/redux-store";
import {setAuthUserData} from "../../Redux/authReducer";


type PropsType = MapStatePropsType & MapDispathPropsType

type DataType = {id: number | null
email: string | null
login: string | null}

type MapStatePropsType = DataType & {

        isAuth: boolean
}
type MapDispathPropsType = {
setAuthUserData: (data: DataType) => void
}

class HeaderContainer extends React.Component<PropsType> {

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/auth/me`, {withCredentials: true})
            .then(response => {
                if (response.data.resultCode === 0) {
                    let data: DataType = response.data.data.login
                    this.props.setAuthUserData(data)
                    debugger
                }
            })
    }

    render() {
        return <Header  />
    }
}

let mapStateToProps = (state: AllAppStateType): MapStatePropsType => {
    return {
        id: state.auth.id,
        email: state.auth.email,
        login: state.auth.login,
        isAuth: state.auth.isAuth
    }
}



// @ts-ignore
export default connect(mapStateToProps, {setAuthUserData})(HeaderContainer)
