import React, {ChangeEvent} from 'react';
import store, {ActionsTypes, PostType, StoreType} from "../../../Redux/redux-store";
import {addPostActionCreator, updateNewPostTextActionCreator} from "../../../Redux/profilReducer";
import MyPosts from "./MyPosts";


type PropsType = {
    posts: Array<PostType>
    dispatch: (action: ActionsTypes) => void
    newPostText: string
    store: StoreType
}


const MyPostsContainer = (props: PropsType) => {
    let state = props.store.getState()

    let addPost = () => {
        props.dispatch(addPostActionCreator())                                                                                                    //функция которая сидит в state (addPost) и добавляет пост после нажатия на кнопку и перерисовывает
    }                                                                                                                        // props.updateNewPost('')      функция обновляет данные в state и перерисовывает пустую строку (перенесли это действие в state
    let onPostChange = (text: string) => {
        let action = updateNewPostTextActionCreator(text)
        props.dispatch(action)                                                                                    //функция которая сидит в state и обновляет данные при написании текста textarea
    }

    return (
       <MyPosts updateNewPostText={onPostChange} addPost={addPost} posts={props.posts} newPostText={props.newPostText} />
    )
};

export default MyPostsContainer;
