import store, {ActionsTypes, PostType, StateType} from "./State";

const UPDATE_NEW_MASSAGE_BODY = 'UPDATE-NEW-MASSAGE-BODY'
const ADD_MASSAGE = 'ADD-MASSAGE'

const dialogReducer = (state: StateType, action: any) => {

     switch (action.type) {
         case UPDATE_NEW_MASSAGE_BODY:
             state.dialogPage.newMassageBody = action.body
         case ADD_MASSAGE: 
         let body = state.dialogPage.newMassageBody
        const newMassage = {
            message: store._state.dialogPage.newMassageBody,
            id: 8
        };
        state.dialogPage.messages.push(newMassage)
        state.dialogPage.newMassageBody = ''                                                                                     // дает  пустую строку
    }
    return state
}

export default dialogReducer
