import React from 'react';
import {addMassageActionCreator, updateNewMassageBodyActionCreator} from '../../Redux/dialogReducer';
import Dialogs from "./Dialogs";
import {Store} from "redux";

type PropsType = {
    store: Store
}
const DialogsContainer = (props: PropsType) => {

    let addMassage = () => {
        props.store.dispatch(addMassageActionCreator())                                                                                                    //функция которая сидит в state (addPost) и добавляет пост после нажатия на кнопку и перерисовывает
    }                                                                                                                        // props.updateNewPost('')      функция обновляет данные в state и перерисовывает пустую строку (перенесли это действие в state
    let onMasssgeChange = (body: string) => {
        props.store.dispatch(updateNewMassageBodyActionCreator(body))                                                                                    //функция которая сидит в state и обновляет данные при написании текста textarea
    }
    return (
    < Dialogs dialogPage={props.store.getState().dialogPage}  updateNewMassageBody={onMasssgeChange} addMassage={addMassage} />
    )
};

export default DialogsContainer;
