import React, {useState} from "react";
import s from './Users.module.css';
import {setUsers, UsersType} from "../../Redux/usersReducer";
import {NavLink} from 'react-router-dom'

type PropsType = {
    setUsers: (users: Array<UsersType>) => void
    totalUsersCount: number
    pageSize: number
    currentPage: number
    users: Array<UsersType>
    follow: (userID: number) => void
    unfollow: (userID: number) => void
    onChangeNumber: (pageNumber: number) => void
}
const Users = (props: PropsType) => {

    let pageCount = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages: number[] = [];

    for (let i = 1; i <= pageCount; i++) {
        pages.push(i)
    }
    let [currentPages, setCurrentPage] = useState(pages.slice(0,10))

    const ChangePagesToRight = () => {
             currentPages = pages.slice(currentPages[0]+ 10,currentPages[0]+ 20 )
         return setCurrentPage(currentPages)
        props.onChangeNumber(currentPages[0]+ 10)
    }
    const ChangePagesToLeft = (num: number) => {
        let arrOfPages = pages
    }



    return <div>
        <div>
            <button onClick={() => ChangePagesToLeft}>left</button>
            {currentPages.map(p => {
                return <span className={props.currentPage === p ? s.selectedPage : ''}
                             onClick={(e) => props.onChangeNumber(p)}>{p} </span>
            })}
            <button onClick={ChangePagesToRight}>right</button>
                </div>
            {props.users.map(u =>
                <div key={u.id}>
                <span>
                <div>
                <NavLink to={'/profil/' + u.id}>
                < img
                src={u.photos.small !== null ? u.photos.small : "https://i.pinimg.com/originals/bd/bf/d0/bdbfd0d90f12604b1538e2049f1c981c.jpg"}
                className={s.img}/>
                </NavLink>
                </div>
                <div>
            {u.followed
                ? <button onClick={() => {
                props.follow(u.id)
            }}>Follow</button>
                : <button onClick={() => {
                props.unfollow(u.id)
            }}>Unfollow</button>
            }
                </div>
                </span>
                <span>
                <span>
                <div>{u.name}</div>
                <div>{u.status}</div>
                </span>
                <span>
                <div>{'u.location.country'}</div>
                <div>{'u.location.city'}</div>
                </span>
                </span>
                </div>
                )}
                </div>

            }

            export default Users
