const SET_USER_DATA = 'SET-USER-DATA'


export type InitialStateUsersType = {
            id: number | null
            email: string | null
            login: string | null
}
let initialState: InitialStateUsersType = {
        id: null,
        email: null,
        login: null
}

const authReducer = (state: InitialStateUsersType = initialState, action: ActionsType): InitialStateUsersType => {
    switch (action.type) {
        case SET_USER_DATA: {
            return {
                ...state,
                ...action.data
            }
        }
    }
}

    export const setUsersData = (id: number, email: string, login: string) => ({type: SET_USER_DATA, data: {id, email, login} as const})


    type ActionsType = ReturnType<typeof setUsersData>

    export default authReducer
