import {ActionsTypes, PostType, ProfilPageType, StateType} from "./redux-store";

const ADD_POST = 'ADD-POST'
const UPDATE_NEW_POST = 'UPDATE-NEW-POST'

let initialState = {
    profilPage: {
        posts: [
            {message: 'Hi, how are you?', id: 1, likecount: "15"},
            {message: "It's my first post", id: 2, likecount: "16"},
            {message: "blablabla", id: 3, likecount: "11"}
        ],
        newPostText: 'it-kamasutra'
    }
}

const profilReducer = (state = initialState, action: any) => {
    switch (action.type) {
        case ADD_POST:
            const newPost: PostType = {
                id: 4,
                message: state.newPostText,
                likecount: '0'
            };
            state.posts.push(newPost)
            state.newPostText = ''                                                                                     // дает  пустую строку
            return state
        case UPDATE_NEW_POST:
            state.newPostText = action.postText
            return state
        default:
            return state
    }
}

export const addPostActionCreator = () => ({type: ADD_POST})
export const updateNewPostTextActionCreator = (text: string) =>
    ({type: UPDATE_NEW_POST, postText: text})

export default profilReducer
