import React from 'react';
import Profil from "./Profil";
import axios from "axios";
import {AllAppStateType} from "../../Redux/redux-store";
import {connect} from "react-redux";
import {setUserProfil} from "../../Redux/profilReducer";

export type UsersPropsType = MapStatePropsType & MapDispathPropsType

type MapStatePropsType = {
    profil: any
}
type MapDispathPropsType = {
    setUserProfil: (profil: any) => void
}

class ProfilContainer extends React.Component<UsersPropsType> {

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/profile/2`)
            .then(resolve => {
                this.props.setUserProfil(resolve.data.profile)
            })
    }
    render() {

        return (
            <div>
                <Profil  {...this.props} profil={this.props.profil}/>
            </div>
        )
    }
}


let mapStateToProps = (state: AllAppStateType): MapStatePropsType => {
    return {
        profil: state.profilPage.profil
    }
}


export default connect(mapStateToProps,
    {
        setUserProfil,
    })(ProfilContainer)
