import React from 'react';
import {connect} from "react-redux";
import {AllAppStateType} from "../../Redux/redux-store";
import Users from "./Users";
import Preloader from "../../common/Preloader";
import {
    follow, unfollow, UsersType, setCurrentPage,
    toggleFollowingInProgress, getUserThunkCreator,
} from "../../Redux/usersReducer";

export type UsersPropsType = MapStatePropsType & MapDispathPropsType
type MapStatePropsType = {
    users: Array<UsersType>
    pageSize: number
    totalUsersCount: number
    currentPage: number
    isFetching: boolean
    followingInProgress: number[]
}
type MapDispathPropsType = {
    follow: (userID: number) => void
    unfollow: (userID: number) => void
    // setUsers: (users: Array<UsersType>) => void            // три сэта перешли в getUserThunkCreator
    // setUsersTotalCount: (totalUsersCount: number) => void
    // toggleIsFetching: (isFetching: boolean) => void
    setCurrentPage: (pageNumber: number) => void
    toggleFollowingInProgress: (isFetching: boolean, userId: number) => void
    getUserThunkCreator: (currentPage: number, pageSize: number) => void
}


class UsersAPIComponent extends React.Component<UsersPropsType> {

    componentDidMount() {
        this.props.getUserThunkCreator(this.props.currentPage, this.props.pageSize)
        // this.props.toggleIsFetching(true)
        // getUsers(this.props.currentPage ,this.props.pageSize)   // axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`,
        //     // {withCredentials: true})
        //     .then(data => {
        //         this.props.toggleIsFetching(false)
        //         this.props.setUsers(data.items)
        //         this.props.setUsersTotalCount(data.totalCount)
        //     })
    }

    onChangeNumber = (pageNumber: number) => {
        this.props.getUserThunkCreator(pageNumber, this.props.pageSize)
        // this.props.toggleIsFetching(true)
        // this.props.setCurrentPage(pageNumber);
        // getUsers(pageNumber,this.props.pageSize )    // axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`,
        //     // {withCredentials: true})
        //     .then(data => {
        //         this.props.toggleIsFetching(false)
        //         this.props.setUsers(data.items)
        //         this.props.setUsersTotalCount(data.totalCount)
        //     })
    }

    render() {
        return <>
            {this.props.isFetching ? <Preloader/> : null}
            <Users totalUsersCount={this.props.totalUsersCount}
                   pageSize={this.props.pageSize}
                   currentPage={this.props.currentPage}
                   users={this.props.users}
                   follow={this.props.follow}
                   unfollow={this.props.unfollow}
                   onChangeNumber={this.onChangeNumber}
                // setUsers={this.props.setUsers}
                   setCurrentPage={this.props.setCurrentPage}
                   followingInProgress={this.props.followingInProgress}
                   toggleFollowingInProgress={this.props.toggleFollowingInProgress}/>
        </>
    }
}

let mapStateToProps = (state: AllAppStateType): MapStatePropsType => {
    return {
        users: state.usersPage.users,
        pageSize: state.usersPage.pageSize,
        totalUsersCount: state.usersPage.totalUsersCount,
        currentPage: state.usersPage.currentPage,
        isFetching: state.usersPage.isFetching,
        followingInProgress: state.usersPage.followingInProgress
    }
}

// const mapDispatchToProps = (dispatch: Dispatch): MapDispathPropsType => {
//     return {
//         follow: (userID) => {
//             dispatch(followAC(userID))
//         },
//         unfollow: (userID) => {
//             dispatch(unFollowAC(userID))
//         },
//         setUsers: (users) => {
//             dispatch(setUsersAC(users))
//         },
//         setCurrentPage: (pageNumber) => {
//             dispatch(setCurrentPageAC(pageNumber))
//         },
//         setUsersTotalCount: (totalCount) => {
//             dispatch(setUsersTotalCountAC(totalCount))
//         },
//         toggleIsFetching: (isFetching) => {
//             dispatch(toggleIsFetching(isFetching))
//         },
//
//     }
//
// }

const UsersContainer = connect(mapStateToProps,
    {
        follow,
        unfollow,
        // setUsers,
        setCurrentPage,
        // setUsersTotalCount,
        // toggleIsFetching,
        toggleFollowingInProgress,
        getUserThunkCreator
    })(UsersAPIComponent)

export default UsersContainer;
