import React, {ChangeEvent} from 'react';
import s from './MyPosts.module.css';
import Post from './Post/Post';
import {ActionsTypes, PostType} from "../../../Redux/State";

type PropsType = {
    posts: Array<PostType>
    dispatch: (action: ActionsTypes) => void
    newPostText: string
}
const MyPosts = (props: PropsType) => {


    let PostsElements = props.posts.map(p => <Post message={p.message} likecount={p.likecount}/>)
    let newPostElement = React.createRef();

    let addPost = () => {
        props.dispatch({type: 'ADD-POST', newPostText: props.newPostText})                                                                                                    //функция которая сидит в state (addPost) и добавляет пост после нажатия на кнопку и перерисовывает
    }                                                                                                                        // props.updateNewPost('')      функция обновляет данные в state и перерисовывает пустую строку (перенесли это действие в state
    let onPostChange = (e: ChangeEvent<HTMLTextAreaElement>) => {
        let text = newPostElement.current.value
        props.dispatch({type: 'UPDATE-NEW-POST', postText: props.newPostText})                                                                                    //функция которая сидит в state и обновляет данные при написании текста textarea
    }

    return (
        <div className={s.mypost}>
            <h3>My posts</h3>
            <div>
                <textarea onChange={onPostChange} ref={newPostElement} value={props.newPostText}/>
            </div>
            <div>
                <button onClick={addPost}>Add post</button>
            </div>
            {PostsElements}
        </div>
    )
};

export default MyPosts;
