 import React from 'react';
import {UsersPropsType} from "./UsersContainer";
import s from './Users.module.css';
import  axios from "axios";



class Users extends React.Component<UsersPropsType> {

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users=page`).then(resolve =>  {this.props.setUsers(resolve.data.items)})
    }

    render() {

        let pageCount: number = Math.ceil(this.props.totalUsersCount / this.props.pageSize);
        let pages: Array<number> = [];

        for (let i = 1 ; i<=pageCount; i++) {
            pages.push(i)
        }

        return (
            <div>
                <div>
                    {pages.map(p => { return <span className={this.props.currentPage === p ? s.selectedPage: ''}>{p}</span>})}
                </div>
                {this.props.users.map(u =>
                        <div key={u.id}>
                <span>
                    <div>
                        < img src={u.photos.small !== null ? u.photos.small : "https://i.pinimg.com/originals/bd/bf/d0/bdbfd0d90f12604b1538e2049f1c981c.jpg"} className={s.img}/>
                    </div>
                    <div>
                        {u.followed
                            ? <button onClick={() => {this.props.follow(u.id)}}>Follow</button>
                            : <button onClick={() => {this.props.unfollow(u.id)}}>Unfollow</button>
                        }
                    </div>
                </span>
                            <span>
                    <span>
                        <div>{u.name}</div>
                        <div>{u.status}</div>
                    </span>
                    <span>
                        <div>{'u.location.country'}</div>
                        <div>{'u.location.city'}</div>
                    </span>
                </span>
                        </div>
                )}
            </div>
        )
    }
}
export default Users
