const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'
const SET_USERS = 'SET-USERS'

export type UsersType = {
    id: number
    followed: boolean
    fullName: string
    status: string
    location: {city: string,
        country: string}
    photoUrl: string
}

export type InitialStateUsersType = {
    users: Array<UsersType>
}
let initialState: InitialStateUsersType = {users:[]
}


const usersReducer = (state: InitialStateUsersType = initialState, action: ActionsType): InitialStateUsersType => {
    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.mp(u => {
                    if (u.id === action.userID) {
                        return {...u, followed: true}
                    } return u
                })
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userID) {
                        return {...u, followed: false}
                    } return u
                })
            }
        case SET_USERS:
            return {...state, users: [ ...state.users, ...action.newUsers]}
        default:
            return state
    }
}

export const followAC = (userID: number) => ({type: FOLLOW, userID } as const)
export const unFollowAC = (userID: number) => ({type: UNFOLLOW, userID} as const)
export const setUsersAC = (newUsers: Array<UsersType>) => ({type: SET_USERS, newUsers} as const)
type ActionsType =
    | ReturnType<typeof followAC>
    | ReturnType<typeof unFollowAC>
    | ReturnType<typeof setUsersAC>

export default usersReducer
