import axios from "axios";
export const getUsers = (currentPage, pageSize) => axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${currentPage}&count=${pageSize}`, {
  withCredentials: true
}).then(response => response.data);
export const setAuthMe = () => axios.get(`https://social-network.samuraijs.com/api/1.0/auth/me`, {
  withCredentials: true
}).then(response => response.data);
