import React from 'react';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import {BrowserRouter} from "react-router-dom";
import ReactDOM from 'react-dom';
 import store, {StateType, StoreType} from "./Redux/redux-store";
import {PropsType} from "./components/Dialogs/DialogItem/DialogItem";
// import store, {StateType} from "./Redux/Store";


  let rerenderEntireTree = (state: PropsType) => {
    ReactDOM.render(
        <React.StrictMode>
            <BrowserRouter>
                <App _state={state} dispatch={store.dispatch.bind(store)} />
                {/*//bind(store) говорит о том что если в обьекте есть this то он всегда относится к store. часто функции */}
                {/*// callback может вызватся в другом месте где будет методом другого обьекта(к примеру props.addPost())*/}
            </ BrowserRouter>,
        </React.StrictMode>, document.getElementById('root'));
}
rerenderEntireTree(store.getState())
store.subscribe(() => {
    let state = store.getState();
    rerenderEntireTree(state)
})
// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
