import React from 'react';
import {
    ActionsType,
    addMassageActionCreator,
    InitialStateType,
    updateNewMassageBodyActionCreator
} from '../../Redux/dialogReducer';
import Dialogs from "./Dialogs";
import {Store} from "redux";
import store, {AllAppStateType} from "../../Redux/redux-store";
import {connect} from 'react-redux';
import {ActionsTypes} from "../../Redux/Store";


type PropsType = {
    store: Store
}
const DialogsContainer = (props: PropsType) => {

    let addMassage = () => {
        props.store.dispatch(addMassageActionCreator())                                                                                                    //функция которая сидит в state (addPost) и добавляет пост после нажатия на кнопку и перерисовывает
    }                                                                                                                        // props.updateNewPost('')      функция обновляет данные в state и перерисовывает пустую строку (перенесли это действие в state
    let onMasssgeChange = (body: string) => {
        props.store.dispatch(updateNewMassageBodyActionCreator(body))                                                                                    //функция которая сидит в state и обновляет данные при написании текста textarea
    }
    return (
        <Dialogs dialogs={props.store.getState().dialogPage.dialogs}
                 messages={props.store.getState().dialogPage.messages}
                 updateNewMassageBody={onMasssgeChange}
                 addMassage={addMassage}
                 newMassageBody={props.store.getState().dialogPage.newMassageBody}/>
    )
};

type MapStatePropsType = {
    dialogPage: InitialStateType
}

type MapDispathPropsType = {
    addMassage: () => void
    onMasssgeChange: (body: string) => void
}


let mapStateToProps = (state: AllAppStateType ): MapStatePropsType => {
    return {
        dialogPage: state.dialogPage
    }
}

const mapDispatchToProps = (dispatch: ActionsType) => {
    return {
        addMassage: () => {
            dispatch(addMassageActionCreator())
        },
        onMasssgeChange: (body: string) => {
            dispatch(updateNewMassageBodyActionCreator(body))
        }


    }
}
 const superDialogContainer = connect(mapStateToProps, mapDispatchToProps)(Dialogs)

export default DialogsContainer;
