import React from 'react';
import s from './MyPosts.module.css';
import Post from './Post/Post';
import {PostType, ProfilPageType} from "../../../Redux/State";

type PropsType = {
    post: Array<PostType>
    addPost: (newPostText: string) => void
    updateNewPost: (newPostText: string) => void
    newPostText: string
}
const MyPosts = (props: PropsType) => {


    let PostsElements = props.post.map(p => <Post message={p.message} likecount={p.likecount}/>)

    let postMessageRef = React.createRef<HTMLTextAreaElement>();

    let addPost = () => {
        props.addPost(props.newPostText)                                                                                                     //функция которая сидит в state (addPost) и добавляет пост после нажатия на кнопку и перерисовывает
    }                                                                                                                        // props.updateNewPost('')      функция обновляет данные в state и перерисовывает пустую строку (перенесли это действие в state


    let onPostChange = () => {

            props.updateNewPost(props.newPostText)                                                                                        //функция которая сидит в state и обновляет данные при написании текста textarea

    }

    return (
        <div className={s.mypost}>
            <h3>My posts</h3>
            <div>
                <textarea onChange={onPostChange} ref={postMessageRef} value={props.newPostText}/>
            </div>
            <div>
                <button onClick={addPost}>Add post</button>
            </div>
            {PostsElements}
        </div>
    )
};

export default MyPosts;
