export type DialogType = {
    name: string
    id: number
    img: string
}
export type MessegeType = {
    message: string
    id: number
}
export type PostType = {
    message: string
    id: number
    likecount: string
}
export type FriendType = {
    name: string
    id: number
    img: string
}
export type SidebarType = {
    friends: Array<FriendType>
}
export type ProfilPageType = {
    posts: Array<PostType>
    newPostText: string

}
export type DialogPageType = {
    dialogs: Array<DialogType>
    messages: Array<MessegeType>
}
export type StateType = {
    profilPage: ProfilPageType
    dialogPage: DialogPageType
    sidebar: SidebarType
}
export type AddPostActionType = {
    type: 'ADD-POST'
    postText: string
}
export type updateNewPostActionType = {
    type: 'UPDATE-NEW-POST'
    postText: string
}
export type StoreType = {
    _state: StateType
    getState: () => StateType
    _callSubscriber: () => void
    updateNewPost: (postText: string) => void
    addPost: (newPost: PostType) => void
    subscribe: (observed: (state: StateType) => void) => void
    dispatch: (action: ActionsTypes) => void
}
export type ActionsTypes = (AddPostActionType | updateNewPostActionType)

let store = {
    _state: {
        profilPage: {
            posts: [
                {message: 'Hi, how are you?', id: 1, likecount: "15"},
                {message: "It's my first post", id: 2, likecount: "16"},
                {message: "blablabla", id: 3, likecount: "11"}
            ],
            newPostText: 'it-kamasutra'
        },
        dialogPage: {
            dialogs: [
                {
                    name: 'Dima',
