    }
    return (
        <Dialogs dialogs={props.store.getState().dialogPage.dialogs}
                 messages={props.store.getState().dialogPage.messages}
                 updateNewMassageBody={onMasssgeChange}
                 addMassage={addMassage}
                 newMassageBody={props.store.getState().dialogPage.newMassageBody}/>
